import React from "react";

import FormInput from "../../components/form-input/form-input.component";
import CustomButton from "../../components/custom-button/custom-button.component";

import {
  ContactPageContainer,
  ContactTitle,
  ContactText,
  ContactFormTitle,
  ContactFormSpan
} from "./contact.style";

const ContactPage = () => {
  const { displayName, email, message } = userCredentials;

  return (
    <ContactPageContainer>
      <ContactTitle>Welcome to a virtual E-Commerce page.</ContactTitle>
      <ContactText>
        Please note that this is a virtual E-Commerce page, based on a Tutorial.
        All products are note real and payments are not really proccesed. Users
        are stored on a Firebase Database.
      </ContactText>
      <ContactText>
        For any further details please do not hesitate to contact me at:
        dieter.halosta@yahoo.com.
      </ContactText>
      <ContactFormTitle>I do not have a account</ContactFormTitle>
      <ContactFormSpan>Sign up with your email and password</ContactFormSpan>
      <form className="contact-form">
        <FormInput
          type="text"
          name="displayName"
          value={displayName}
          onChange={handleChange}
          label="Your Name"
          required
        />
        <FormInput
          type="email"
          name="email"
          value={email}
          onChange={handleChange}
          label="Your Email"
          required
        />
        <FormInput
          type="text"
          name="Message"
          value={message}
          onChange={handleChange}
          label="Message"
          required
        />
        <CustomButton type="submit">SEND MESSAGE</CustomButton>
      </form>
    </ContactPageContainer>
  );
};

export default ContactPage;
